    /**
     * Test set up method
     */
    protected function setUp()
    {
        parent::setUp();
        $this->module = Shopware()->Modules()->Articles();
    }

    /**
     * Retrieve module instance
     *
     * @return sArticles
     */
    public function Module()
    {
        return $this->module;
    }

    public function testGetArticles() {
        foreach($this->categories as $id => $expected) {
            if (strpos($expected['ordernumber'], 'swTEST') !== false) {
                continue;
            }
            $data = $this->Module()->sGetArticlesByCategory($id);
            $this->assertCount(count($data['sArticles']), $expected['sArticles']);
            $this->assertArticles($expected['sArticles'], $data['sArticles']);
            $this->assertEquals($expected['sPage'], $data['sPage']);
            $this->assertEquals($expected['sNumberArticles'], $data['sNumberArticles']);
            $this->assertEquals($expected['sPerPage'], $data['sPerPage']);
        }
    }

    protected function assertArticles($expected, $data)
    {
        $count = count($expected);
        for ($i = 0; $i < $count; $i++) {
            $expectedArticle = array_shift($expected);
            $gotArticle = array_shift($data);

            foreach($this->articlePropertiesAssert as $property) {
                $this->assertEquals($expectedArticle[$property], $gotArticle[$property]);
            }
        }
    }

